import './style.css';
import React, { useRef } from 'react';


function App() {


  const STARTING_TIME = 5;

  const[text,setText]=React.useState("")
  const[time,setTime]=React.useState(STARTING_TIME);
  const[isTimeRunning, setisTimeRunning]=React.useState(false);
  const[wordC, setWordC]=React.useState(0);
   const Ref=useRef();





function handleChange(e)
{


setText(e.target.value)

}



React.useEffect(()=>{


  if(isTimeRunning && time>0)
  {

  setTimeout(() => {
   setTime(time => time - 1)
}, 1000)

  }

  else if(time===0)
  {

    endGame()

  }


}, [time, isTimeRunning])



function calculateWordCount(text) {
  const wordsArr = text.trim().split(" ")
  return wordsArr.filter(word => word !== "").length
}


function startGame()

{

setWordC(0);
  setisTimeRunning(true);
  setTime(STARTING_TIME);
  setText("");
  Ref.current.disabled=false;
  Ref.current.focus();


}

function endGame()
{

setisTimeRunning(false)
setWordC(calculateWordCount(text));

}


  return (
    <div>
    <h1>How fast do you type?</h1>
    <textarea
        onChange={handleChange}
        value={text}
        ref={Ref}
        disabled={!isTimeRunning}
    />
    <h4>Time remaining: {time} </h4>
    <button disabled={isTimeRunning} onClick={startGame}>
        Start
    </button >
    <h1>Word count : {wordC}</h1>
</div>
  )
}

export default App;
